let header = document.getElementById('header')
let logo = document.getElementById('logo')
let menuList = document.getElementsByClassName('nav-option')

let about = document.getElementById('about')
let drop_down = document.getElementById('drop-down')

window.addEventListener('scroll' , ()=>{
    let scrollValue = window.scrollY
    console.log(scrollValue);
    for (let n = 0; n < menuList.length; n++) {
        if (scrollValue < 100) {
            header.classList.remove('bgColor')
            logo.removeAttribute('src')
            logo.setAttribute('src' , './assets/logo-dark-new-04.svg')
            logo.style.width = '27%'
            menuList[n].style.color = '#fff'
            drop_down.style.top = '70px'
        } else {
            header.classList.add('bgColor')
            logo.removeAttribute('src')
            logo.setAttribute('src' , './assets/logo-dark.png')
            logo.style.width = '20%'
            menuList[n].style.color = '#000'
            drop_down.style.top = '40px'
        }
        
    }
})


